import argparseimport jsonimport millennium_falconfrom quadratic_equation import solve_quadratic_equationif __name__ == '__main__':    parser = argparse.ArgumentParser(description='Quadratic Equation Solver')    parser.add_argument('-a', type=float, default=0, help='coefficient a')    parser.add_argument('-b', type=float, required=True, help='coefficient b')    parser.add_argument('-c', type=float, required=True, help='coefficient c')    args = parser.parse_args()    a = args.a    b = args.b    c = args.c    x1, x2 = solve_quadratic_equation(a, b, c)    if x1 is None:        print("The equation has no real solutions.")    else:        print("The solutions to the equation are:")        print("x1 =", x1)        if x2 is not None:            print("x2 =", x2)    ship_info = millennium_falcon.get_ship_info()    if ship_info:        print("\nMillennium Falcon Information:")        print("Name:", ship_info["name"])        print("Max Speed:", ship_info["max_speed"])        print("Class:", ship_info["class"])        print("Pilots:")        for pilot in ship_info["pilots"]:            print("Name:", pilot["name"])            print("Height:", pilot["height"])            print("Weight:", pilot["weight"])            print("Homeworld:", pilot["homeworld"])            print("Homeworld Info:", pilot["homeworld_info"])        with open("millennium_falcon.json", "w") as file:            json.dump(ship_info, file, indent=4)###